import React, { Component } from 'react'

export default class Comment extends Component {

  setComment = (e) => {
    this.setState({
      comment: e.target.value
    })
  }
  render() {
    return (
      <div>
        <input type="text" onChange={this.setComment} />
      </div>
    )
  }
}
