import React, { Component, Fragment } from 'react'
import api from '../../api'
import { Link } from 'react-router-dom'

export default class GoalID extends Component {

  constructor(props) {
    super(props)
    this.state = {
      goalzz: {}
    }
  }


  componentDidMount() {
    console.log(this.props.match.params.id)
    // console.log('The Goal ID is', this.props.match.params.id)
    console.log(this)
    api.getTheGoal(this.props.match.params.id)
      .then(goalzz => {
        console.log(goalzz)
        this.setState({
          goalzz
        })
        console.log(this)
      })
      .catch(err => console.log(err))
  }

  showAGoal = () => {
    let aGoal = this.state.goalzz.name;
    return (
      <div className='card'>
        <h3 className='card-title'>{aGoal.name}</h3>
        <h4 className='card-subtitle'>Partnered with: {aGoal.partner}</h4>
        <h5 className='card-text'>Description: {aGoal.description}</h5>
        <h6 className='card-text'>Date Created: {aGoal.date}</h6>
        {/* <button onClick={() => this.deleteGoal(aGoal._id)} className="btn btn-primary" >Delete</button> */}
      </div>
    )
  }


  render() {
    return (
      <Fragment>
        <div>
          {this.showAGoal()}
          {/* <h2>The Goal Details</h2>
          <div className='card-deck'>
            <div className='card'>
              <div className='card-body'>
                {this.state.goalzz.name}
              </div>
            </div>
          </div> */}
        </div >
      </Fragment>
    )
  }
}
