import React, { Component, Fragment } from 'react'
import api from '../../api'

export default class GoalID extends Component {

  constructor(props) {
    super(props)
    this.state = {
      goals: []
    }
  }


  componentDidMount() {

    console.log('The Goal ID is', this.props.match.params.id)
    api.getTheGoal(this.props.match.params.id)
      .then(goalzz => {
        console.log(goalzz)
        this.setState({
          goals: goalzz
        })
      })
    console.log('test')
      .catch(err => console.log(err))
  }

  render() {
    return (
      <Fragment>
        <div className='page'>
          {/* Style with bootstrap cards */}
          <h2>The Goal</h2>
          <div className='card-deck'>
            <div className='card'>
              <div className='card-body'>
                {this.state.goals.map((aGoal, i) => <div>
                  <div className='card-title'>{aGoal.name}</div>
                  <div className='card-subtitle'>{aGoal.partner}</div>
                  <div className='card-text'>{aGoal.description}</div>
                  <div className='card-text'>{aGoal.date}</div>
                  <button onClick={() => this.deleteGoal(aGoal._id, i)} className="btn btn-primary" >Delete</button>
                </div>)}
              </div>
            </div>
          </div>
        </div >
      </Fragment >
    )
  }
}
