import api from '../../api';
import Axios from 'axios'

export default class MyGoals extends Component {
  constructor(props) {
    super(props)
    this.state = {
      goals: []
    }
  }
  componentDidMount() {
    api.getMyGoals()
      .then(goalzz => {
        console.log(goalzz)
        this.setState({
          goals: goalzz
        })
      })
      .catch(err => console.log(err))
  }

  showTheGoals = () => {
    return this.state.goals.map(aGoal => {
      return <div className='card card-fit'>
        {/* <img src={aGoal.image_url} className='card-img-top img-fit' /> */}
        <h3 className='card-title'>({aGoal.name})</h3>
        <h4 className='card-subtitle'>({aGoal.partner})</h4>
        <h5 className='card-text'>First Brewed on: ({aGoal.description})</h5>
        <h6 className='card-text'>Attenuation Level: ({aGoal.date})</h6>
      </div>
    })
  }

  deleteGoal = (id, i) => {
    console.log(id)
    Axios.post('http://localhost:5000/api/mygoals/deleteGoal', { id: id }).then(responseFromServer => {
      console.log(responseFromServer)
      let newGoals = [...this.state.goals]
      this.state.goals.splice(i, 1)
      this.setState({ newGoals })
    })
  }


